mapper.select:
select a.CLIENT_ID as clientId, a.RESOURCE_IDS as resourceIds, a.CLIENT_SECRET as clientSecret, a.SCOPE as scope, a.AUTHORIZED_GRANT_TYPES as authorizedGrantTypes, a.WEB_SERVER_REDIRECT_URI as webServerRedirectUri, a.AUTHORITIES as authorities, a.ACCESS_TOKEN_VALIDITY as accessTokenValidity, a.REFRESH_TOKEN_VALIDITY as refreshTokenValidity, a.ADDITIONAL_INFORMATION as additionalInformation, a.AUTOAPPROVE as autoapprove, a.STATUS as status
from T_SYS_OAUTH_CLIENT_DETAILS a where 1=1
AND a.CLIENT_ID = #{entity.clientId}
AND a.AUTOAPPROVE = #{entity.autoapprove}
AND a.STATUS = #{entity.status}
order by a.CLIENT_ID
